import { Component, OnInit } from '@angular/core';
import { IFilm } from 'src/app/shared/models/film.model';
import { TitlesStreamService } from 'src/app/shared/services/titles-stream.service';
import { CategoriesFilmService, FilmCategories } from '../services/categories-film.service';

@Component({
  selector: 'app-animazione',
  template: `
      <app-subsection-category [sectionTitle]="'Animazione'" [titles]="titles"></app-subsection-category>
  `,
  styles: [
  ]
})
export class AnimazioneComponent implements OnInit {
  public titles:IFilm[] = [];

  constructor(private titlesStream:TitlesStreamService, private categoryFilm:CategoriesFilmService) { }
  private getOwnCategoryFilm(ownCategory:FilmCategories) {
    this.categoryFilm.getSpecificCategoryFilm(ownCategory)
  }
  ngOnInit(): void {
    this.titlesStream.getAll().subscribe(data =>
      this.titles = data[2]);
      console.log(this.titles)
  }

}
